import Example from './example';

let store = null;

export default class Store {

    constructor(example) {
        this.Example = new Example(example);
    }
}

export function initStore(isServer, example) {
    if (isServer && typeof window === 'undefined') {
        return new Store(example);
    }
    else {
        if (store === null) {
            store = new Store(example);
        }
        return store;
    }
}
