import React from 'react'
import { inject, observer } from 'mobx-react'
import Link from 'next/link';
import '../styles/style.scss'

@inject('store')
@observer
class Page extends React.Component {

    handleAddFavorite = (event, filmId) => {
        console.log(event.target.className);
        event.target.className = event.target.className === 'far fa-star favorite-button' && 'fas fa-star favorite-button';
    }
    render() {
        const films = this.props.store.films;
        return (
            < div className='container' >
                {
                    this.props.store.films.length === undefined
                        ? <div className='searchSomethingDiv'><h2>Let's search something!</h2></div>
                        : (
                            <div className='row'>
                                {
                                    films.map((film, index) => (
                                        <div key={index} className='col-md-4 image-box'>
                                            <div className="card bg-secondary text-white card-width">
                                                <img className="card-img-top" width='50px' height='300px' src={film.Poster} alt="Card image cap" />
                                                <div className="card-body">
                                                    <h5 className="card-title">{film.Title}</h5>
                                                    <p className="card-text">{film.Year}</p>
                                                    <a href="#"><i onClick={(event) => this.handleAddFavorite(event, film.imdbID)} className="far fa-star favorite-button"></i></a>
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                }
                            </div>

                        )
                }
            </div>
        )
    }
}

export default Page
