import React from 'react'
import { inject, observer } from 'mobx-react'
import Link from 'next/link';
import '../styles/style.scss'
import fetch from 'isomorphic-unfetch'

@inject('store')
@observer
class Page extends React.Component {

    state= {
        films : []
    }
    asd = async () => {
        console.log('asdasd')
        const response = await fetch('http://www.omdbapi.com?apikey=f976e35d&s=Godfather&type=movie');
        const {Search} = await response.json();
        this.setState({
            films: Search
        });
        console.log('Data : ',Search);
        console.log('films:' ,this.state.films);
    }
    render() {
        console.log('index:',this.props.store);
        return (
            <div className='container'>
                <div className='row'>
                    <div className='col-md-4 image-box'>
                        <div className="card bg-secondary text-white card-width">
                            <img className="card-img-top" width='50px' height='300px' src="https://m.media-amazon.com/images/M/MV5BMDQ5ZjNkYzYtMTQ2My00ZjU0LTk0MWQtNjhhNTg5ZWQzYzEyXkEyXkFqcGdeQXVyMjA0MDQ0Mjc@._V1_SX300.jpg" alt="Card image cap" />
                            <div className="card-body">
                                <h5 className="card-title">Card title</h5>
                                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <button onClick={this.asd}><i className="far fa-star favorite-button"></i></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Page
