import React, { Component } from 'react'
import Link from 'next/link';


class Navbar extends Component {

    state = {
        searchValue: ''
    }

    handleSearchChange = async (event) => {
       await    this.setState({
           searchValue: event.target.value
       });
       console.log(this.state.searchValue);
    }

    render() {
        return (
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <Link href='/'>
                    <a className='navbar-brand'>Imdb Project</a>
                </Link>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav mr-auto">
                        <Link href='/other'>
                            <a className='nav-item nav-link'>My Favorites</a>
                        </Link>
                    </ul>
                    <form className="form-inline my-2 my-lg-0">
                        <input value={this.state.searchValue} onChange={this.handleSearchChange} className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                        <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                    </form>
                </div>
            </nav>
        )
    }
}

export default Navbar;
