import React, { Component } from 'react'

class FilmBox extends Component {
    render() {
        return (
            <div>
                <div className='row'>
                    <div key={this.props.key} className='col-md-4 image-box'>
                        <div className="card bg-secondary text-white card-width">
                            <img className="card-img-top" width='50px' height='300px' src={this.props.poster} alt="Card image cap" />
                            <div className="card-body">
                                <h5 className="card-title">{this.props.title}</h5>
                                <p className="card-text">{this.props.year}</p>
                                <a href="#"><i onClick={(event) => this.handleAddFavorite(event, film.imdbID)} className="far fa-star favorite-button"></i></a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default FilmBox;
